import React , { Component, useContext, useState } from 'react';
//import {ThemeProvider as MuiThemeProvider} from '@material-ui/core/styles/MuiThemeProvider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/core/icons/Menu';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';

import {GlobalContext} from '../context/GlobalState';


export const UserDetails = (props) => {
    const [state , setState] = useState({
        username: ''
    })

    const { getUserId , userId} = useContext(GlobalContext)


    /*continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }*/

    /*async componentDidMount(){
        const items = await axios.get('/api/v1/users')
        this.state.items.push(items.data.data[0])
    }*/

    const handleChange = e => {
        setState({
            username: e.target.value
        })
    }

    const handleLogin = () => {
        getUserId(state.username)
        if(userId) props.nextStep()
    }

    /*handleLogin = () => {
        /*const res = await axios.post('/api/v1/users',
        {
            username: this.state.username
        })
       const currentUser = state.items.filter((item) => {
            return state.username === item.username
        })[0];
        setState({
            userId: currentUser._id
        })
    } */

        return (

                <React.Fragment>
                    <AppBar position="static">
                        <Toolbar>
                        <IconButton edge="start" className={{marginRight:"theme.spacing(2)"}} color="inherit" aria-label="menu">
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" className={{flexGrow:"1"}}>
                            Enter User Details
                        </Typography>
                        </Toolbar>
                    </AppBar>
                    <TextField
                    id='standard-basic' 
                    hintText = 'Enter Your Username'
                    label = 'Username'
                    onChange={handleChange}
                    value = {state.username}
                    />
                    <br/>

                    <Button variant='contained' color='primary' 
                    label = 'Continue'
                    onClick = {()=>handleLogin()}
                    style = {styles.button}
                    >
                        Login
                    </Button>
                </React.Fragment>
        )


}

const styles = {
    button: {
        margin : 15
    }
}
export default UserDetails;
