import React , { useContext, useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid'

import {GlobalContext} from '../context/GlobalState';


export const Register = (props) => {
    const [state , setState] = useState({
        username: '',
        password: ''
    })

    const { register , userId} = useContext(GlobalContext)

    const handleChange = e => {
        setState({
            username: e.target.value
        })
    }

    const handleRegister = () => {
        register(state)
        if(userId) props.hasAuthenticatedStep()
    }

       return (

                <React.Fragment>
                    <AppBar position="static">
                        <Toolbar>
                        <IconButton edge="start" className={{marginRight:"theme.spacing(2)"}} color="inherit" aria-label="menu">
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" className={{flexGrow:"1"}}>
                            Enter User Details
                        </Typography>
                        </Toolbar>
                    </AppBar>
                    <Grid container justify="center" className={{marginTop:"theme.spacing(2)"}}>
                        <Box mx="auto">
                            <TextField
                            id='standard-basic' 
                            hintText = 'Enter Your Username'
                            label = 'Username'
                            onChange={handleChange}
                            value = {state.username}
                            />
                            <br/>
                            <TextField
                            id='standard-basic' 
                            hintText = 'Password'
                            label = 'Password'
                            onChange={handleChange}
                            value = {state.password}
                            />
                            <br/>
                            <Button variant='contained' color='primary' 
                            label = 'Continue'
                            onClick = {()=>handleRegister()}
                            style = {styles.button}
                            >
                                Login
                            </Button>
                        </Box>
                    </Grid>
                </React.Fragment>
        )


}

const styles = {
    button: {
        margin : 15
    }
}
export default Register;
