const bcrypt = require('bcryptjs')
const saltRounds = 10
const plainTextPassword = 'theboys'

function encrypt(plainTextPassword){
  return new Promise((resolve , reject)=>{
    bcrypt.hash(plainTextPassword , saltRounds  , (err , res)=>{
      if (err) reject(err)
      else {
        resolve(res)
      }
    });

  })
}

function decrypt(plainTextPassword , hashedPassword){
  return new Promise((resolve , reject) => {
    bcrypt.compare(plainTextPassword , hashedPassword , (err , res)=>{
      if (err) reject(err)
      else{
        resolve(res)
      }
    })
  })
}
