import React , { useReducer, createContext } from 'react';
import AppReducer from './AppReducer';
import axios from 'axios';

const initialState = {
    transactions: [],
    firstName: '',
    userId: '',
    error: null,
    loading: true,
    step: 1
};
// create context
export const GlobalContext = createContext(initialState);

// provider 
export const GlobalProvider = ({ children }) => {
    const [state , dispatch ] = useReducer( AppReducer , initialState);

    async function getTxns(){
            try {
                const res = await axios.get(`/api/v1/txns`);
                dispatch({
                    type: 'GET_TXNS',
                    payload: res.data.data
                });
            } catch (err) {
                dispatch({
                    type: 'GET_ERR',
                    payload: console.log(err)
                });
            }

    }

    async function deleteTransaction(id){
            try {
                await axios.delete(`/api/v1/txns/${id}`);
                dispatch({
                    type: 'DELETE_TXN',
                    payload: id
                });

            } catch (err) {
                dispatch({
                    type: 'GET_ERR',
                    payload: console.log(err)
                });
            }
    }

    async function addTransaction(transaction){
        const config = {
            headers:{
                "Content-Type":"application/json"
            }
        }
            try {
                const res = await axios.post(`/api/v1/txns`, transaction, config);
                dispatch({
                    type: 'ADD_TXN',
                    payload: res.data.data
                });
                console.log(res)
            } catch (err) {
                dispatch({
                    type: 'GET_ERR',
                    payload: console.log(err)
                });

            }
    }

    async function login(data){
        try {
            const res = await axios.post('api/v1/users/login' , data)
            console.log(res);
            //const currentUser = res.data.data.filter(item=> item.username === username)[0]
            dispatch({
                type: 'LOGIN',
                payload: res.data.data
            })
            console.log(state);
        } catch (error) {
            dispatch({
                type: 'GET_ERR',
                payload: console.log(error)
            })
        }
    }

    async function dateFilter(date){
        const startDate = new Date(date)
        const year = startDate.getFullYear()
        const month = startDate.getMonth()
        try {
           const res = await axios.get('api/v1/txns/sortDate' , {params: {startDate , endDate: new Date(year , month + 1)}})
            dispatch({
                type: 'DATE_FILTER',
                payload: res.data.data
            })      
        } catch (error) {
            dispatch({
                type: 'GET_ERR',
                payload: console.log(error)
            })
        }
    }

    async function register(data){
        if(data){
            try {
                const res = await axios.post('api/v1/users/register', data);
                dispatch({
                    type: 'LOGIN_STEP'
                })
            } catch (error) {
                dispatch({
                    type: 'GET_ERR',
                    payload: console.log(error)
                })
            }
        }
    }

    function actionStep() {
        dispatch({
            type: 'ACTION_STEP'
        })
    }

    function loginStep() {
        dispatch({
            type: 'LOGIN_STEP'
        })
    }

    function registerStep(){
        dispatch({
            type: 'REGISTER_STEP'
        })
    }

    return (
        <GlobalContext.Provider value={{
            transactions: state.transactions,
            error: state.error,
            loading: state.loading,
            userId: state.userId,
            firstName: state.firstName,
            step: state.step,
            deleteTransaction,
            addTransaction,
            getTxns,
            login,
            register,
            dateFilter,
            actionStep,
            loginStep,
            registerStep
            }}>
            {children}
        </GlobalContext.Provider>
    )
}
export default GlobalProvider;
