import React , { Component, useContext } from 'react';
//import {ThemeProvider as MuiThemeProvider} from '@material-ui/core/styles/MuiThemeProvider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/core/Menu';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';

import GlobalContext from '../context/GlobalState';


class UserDetails extends Component {
    constructor(props){
        super(props)
        this.state = {
            username: '',
            items: [],
            userId: ''
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleLogin = this.handleLogin.bind(this);
    }

    /*continue = e => {
        e.preventDefault();
        this.props.nextStep();
    }*/

    async componentDidMount(){
        const items = await axios.get('/api/v1/users')
        this.state.items.push(items.data.data[0])
    }

    handleChange = e => {
        this.setState({
            username: e.target.value
        })
    }

    handleLogin = () => {
        /*const res = await axios.post('/api/v1/users',
        {
            username: this.state.username
        })*/
       const currentUser = this.state.items.filter((item) => {
            return this.state.username === item.username
        })[0];
        this.setState({
            userId: currentUser._id
        })
    }

    render(){

        const {getUserId} = useContext(GlobalContext)

        return (

                <React.Fragment>
                    <AppBar position="static">
                    <Toolbar>
                      <IconButton edge="start" className={{marginRight:"theme.spacing(2)"}} color="inherit" aria-label="menu">
                        <MenuIcon />
                      </IconButton>
                      <Typography variant="h6" className={{flexGrow:"1"}}>
                        Enter User Details
                      </Typography>
                    </Toolbar>
                    </AppBar>
                    <TextField
                    id='standard-basic' 
                    hintText = 'Enter Your Username'
                    label = 'Username'
                    onChange={this.handleChange}
                    value = {this.state.username}
                    />
                    <br/>

                    <Button variant='contained' color='primary' 
                    label = 'Continue'
                    onClick = {getUserId(this.state.username)}
                    style = {styles.button}
                    >
                        Login
                    </Button>
                </React.Fragment>
        )

    }
}

const styles = {
    button: {
        margin : 15
    }
}
export default UserDetails;
