import React , { useContext, useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import {ArrowBack} from '@material-ui/icons'

import {GlobalContext} from '../context/GlobalState';

import {decrypt} from '../utils/encrypt';

export const Login = (props) => {
    const [state , setState] = useState({
        username: '',
        plainPassword: ''
    })

    const { login , password , userId} = useContext(GlobalContext)

    const handleChange = e => {
        const value = e.target.value
        setState({
            [e.target.name]: value
        })
    }

    const handleLogin = async() => {
        await login(state)
        console.log(password) //props.hasAuthenticatedStep()
        /*if(password){
            const match = await decrypt(state.plainPassword , password).then(res => res).catch(err => console.log(err))
            console.log(match)
            if(match) props.hasAuthenticatedStep()
            else{
                window.alert('Wrong Password! Enter Correct Password')
                setState({
                    username: '',
                    plainPassword: ''
                })
            }
        }*/
    }

    const handleBack = () => {
        props.actionStep()
      }

    return (
        <React.Fragment>
            <AppBar position="static">
                <Toolbar>
                <IconButton edge="start" className={{marginRight:"theme.spacing(2)"}} color="inherit" aria-label="menu">
                    <MenuIcon />
                </IconButton>
                <Typography variant="h6" className={{flexGrow:"1"}}>
                    Enter User Details
                </Typography>
                </Toolbar>
            </AppBar>
            <Grid container justify="center" className={{marginTop:"theme.spacing(2)"}}>
                <Box mx="auto">
                    <TextField
                    id='standard-basic' 
                    hintText = 'Enter Your Username'
                    label = 'Username'
                    name = 'username'
                    onChange={handleChange}
                    value = {state.username}
                    />
                    <br />
                    <TextField
                    id='standard-basic' 
                    hintText = 'Enter Your Password'
                    label = 'Password'
                    name = 'plainPassword'
                    onChange={handleChange}
                    value = {state.plainPassword}
                    />
                    <br/>
                    <Button variant='contained' color='primary' 
                    label = 'Continue'
                    onClick = {()=>handleLogin()}
                    style = {styles.button}
                    >
                        Login
                    </Button>
                    <br />
                    <br />
                    <ArrowBack onClick={()=>handleBack()}>Back</ArrowBack>
                </Box>
            </Grid>
        </React.Fragment>
    ) 
}

const styles = {
    button: {
        margin : 15
    }
}
export default Login;
