import { FormControl, InputLabel, MenuItem, Select } from '@material-ui/core';
import React , { useContext , useEffect , useState } from 'react';
import '../App.css';

import { GlobalContext } from '../context/GlobalState';
import { Transaction } from './Transaction';

const TransactionList = () => {

    const [TxnDate , setTxnDate] = useState('')
    const [open , setOpen] = useState(false)
    const { transactions , getTxns, dateFilter }  = useContext(GlobalContext);

    const handleSelect = (e) => {
        setTxnDate(e.target.value);
        if(e.target.value === '') getTxns();
        dateFilter(e.target.value);
    }

    const handleOpen = () => setOpen(!open)

  useEffect(() => {
    getTxns();
        },[ ]) ; 


    return (
        <div className='container'>
            {/*<div className='container' style={{display:"flex" ,flexDirection: "row"}}>*/}
                <h3>History</h3>
                <FormControl>
                    <InputLabel id="date-filter-label">Filter By Date</InputLabel>
                    <Select
                        open = {open}
                        onClick={()=>handleOpen()}
                        style={{width:"350px" , paddingTop:"10px"}}
                        labelId="date-filter-label"
                        label="Filter By Date"
                        value={TxnDate}
                        onChange={handleSelect}
                        >
                            <MenuItem value={''}><em>None</em></MenuItem>
                            <MenuItem value={'2021-01'}>January 2021</MenuItem>
                            <MenuItem value={'2021-02'}>February 2021</MenuItem>
                            <MenuItem value={'2021-03'}>March 2021</MenuItem>
                            <MenuItem value={'2021-04'}>April 2021</MenuItem>
                    </Select>
                </FormControl>
               {/* <div style={{marginLeft: "100px"}}>


    </div> 
    </div>*/}
            <ul id='list' className='list'>
                {transactions.map((transaction) => (<Transaction key={transaction._id} transaction={transaction}/>))}
            </ul>
        </div>
    )
}

export default TransactionList;
