import React , { useContext } from 'react';

import { GlobalContext } from '../context/GlobalState';

const TransactionList = () => {

    const { transactions }  = useContext(GlobalContext);

    return (
        <>
            <h3>History</h3>
            <ul id='list' className='list'>
                {transactions.map(() => (<li className='minus' key={transactions.transactions.id}> {transactions.transactions.text} 
                    <span>{transactions.transactions.amount}</span><button className='delete-btn'>&times;</button>
                </li>))}
            </ul>
        </>
    )
}

export default TransactionList;
