const bcrypt = require('bcryptjs')
const saltRounds = 10

export function encrypt(plainTextPassword){
  bcrypt.hash(plainTextPassword , saltRounds , null , (err , res)=>{
    if (err) return err
    if (res) return res
  })
}

export function decrypt(plainTextPassword , hashedPassword){
  bcrypt.compare(plainTextPassword , hashedPassword , (err , res)=>{
    return res
  })
}
