import React , { useContext }from 'react';

import {GlobalContext} from '../context/GlobalState';

export const Transaction = ({transaction}) => {
    const sign = transaction.amount < 0 ? '-' : '+';
    const clname = transaction.amount < 0 ? 'minus' : 'plus';

    const deleteAction = useContext(GlobalContext);

    return(
        <li className={clname} key={transaction.id}> {transaction.text} 
            <span>{sign}${Math.abs(transaction.amount)}</span><button className='delete-btn' onClick={()=> deleteAction(transaction.id)}>&times;</button>
        </li>
    )
}

export default Transaction;
